    const [somedays, setSomedays] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const doOnsHelper = [];
        const doBysHelper = [];
        const somedaysHelper = [];

        // deviding the tasks by type and setting it in states
