    return(
        <div>
            <form action="">
            <div>
                <label> Title </label>
                <h1>{ title }</h1>
                    {!editTitle?
                        <button onClick={()=> setEditTitle(true)}>Edit</button>
                    :
                        <div>
                            <input 
                                type="text" 
                                name="newTitle" 
                                value={title} 
                                onChange={(e)=> setTitle(e.target.value)}
                                />
                            <button onClick={()=> {
                                setEditTitle(false)
                                onClickingDone()
                                }}>Done</button>
                        </div>

                    }
            </div>
            <div>
                <label> Colour </label>
                <svg viewbox="0 0 100 60" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="20" cy="20" r="20" fill={colour}/>
                </svg>
                {!editColour?
                        <button onClick={()=> setEditColour(true)}>Edit</button>
                    :
                        <div>
                            <p>Choose a new colour</p>
                            <input 
                                type="color" 
                                name="newColour" 
                                value={colour} 
                                onChange={(e)=> setColour(e.target.value)}
                                />
                            <button onClick={()=> {
                                setEditColour(false)
                                onClickingDone()
                                }}>Done</button>
                        </div>

                    }
            </div>
            </form>
        </div>
    )
}

export default AddNewCategoryPage;
