        navigate(`/category/:${category.title}`, {
            state:{
                category: category,
                categories: categories,
                priorities: priorities
            }
        });

    }

    return(
        <div>
            <h1>
                <button onClick={ goToDistinctCategoryPage } >{category.title}</button>
            </h1>
        </div>
    )
}

export default Category;
