                priorities: priorities,
            }
        })
    }

    return (
            <div>
                <input type="checkbox" id="task" name="task"/>
                <label htmlFor="task"> <button onClick={onTaskClick}>{ task.title }</button> </label>
            </div>
      )
}

export default Task;
